mapper.select:
SELECT 

FROM tb_issue a

WHERE a.id = #{id}

SELECT 

FROM tb_issue a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.issue_title LIKE 
			'%'||#{issueTitle}||'%'
			'%'+#{issueTitle}+'%'
			concat('%',#{issueTitle},'%')


		AND a.issue_status = #{issueStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tb_issue a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tb_issue SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
