mapper.select:
SELECT 

FROM tb_school a

WHERE a.id = #{id}

SELECT
FROM tb_school a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.department_name LIKE '%'||#{departmentName}||'%' '%'+#{departmentName}+'%' concat('%',#{departmentName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM tb_school a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tb_school SET 	
	department_name = #{departmentName},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE tb_school SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
