mapper.select:
SELECT 

FROM tb_student a

WHERE a.id = #{id}

SELECT 

FROM tb_student a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.account = #{account}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tb_student a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tb_student SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
