mapper.select:
SELECT 

FROM tb_task a

WHERE a.id = #{id}

SELECT
FROM tb_task a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.task_title LIKE '%'||#{taskTitle}||'%' '%'+#{taskTitle}+'%' concat('%',#{taskTitle},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM tb_task a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tb_task SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
