mapper.select:
SELECT 

FROM tb_teacher a

WHERE a.id = #{id}

SELECT
FROM tb_teacher a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.teacher_name LIKE '%'||#{teacherName}||'%' '%'+#{teacherName}+'%' concat('%',#{teacherName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM tb_teacher a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tb_teacher SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
